#include "nr-card.h"
#include <glib.h>
#include <giomm/memoryinputstream.h>

#include <unistd.h>
#include <iostream>

NrCard::NrCard(const NrCard& a) : base64Buffer(0)
{
	name = a.name;
	keywords = a.keywords;
	gameText = a.gameText;
	rulingText = a.rulingText;
	flavorText = a.flavorText;
	image = a.image;
}

NrCard::~NrCard()
{
	if (base64Buffer) g_free(base64Buffer);
}

static const char * sample_data = "";

NrCard* NrCard::Sample()
{
	NrCard* l = new NrCard;

	std::cout << "cwd: " << get_current_dir_name() << std::endl;

	guchar * data;
	gsize out_len;

	data = g_base64_decode(sample_data, &out_len);
	Glib::RefPtr<Gio::MemoryInputStream> st = Gio::MemoryInputStream::create();
	st->add_data(data, out_len, g_free);
	l->image = Gdk::Pixbuf::create_from_stream(st);
	//l->image = Gdk::Pixbuf::create_from_inline(out_len, data, true);
	//g_free(data);
	
	//l->image = Gdk::Pixbuf::create_from_file("sample/netrunner-day-shift.jpg");

	//std::cout << "image: " << l->Base64Image() << std::endl;

	return l;
}

gchar* NrCard::Base64Image()
{
	if (!base64Buffer)
	{
		gchar* lBuffer = 0;
		gsize lLen = 0;
		try {
			image->save_to_buffer(lBuffer, lLen, "jpeg");
			base64Buffer = g_base64_encode((guchar*)lBuffer, lLen);
		} catch (const Gdk::PixbufError& e) {
			std::cout << e.what() << std::endl;
		}
	}
	return base64Buffer;
}

